int attempts=3;

double ma;

double computeMA(){
   int i;
   double val=0.0;
   for(i=0;i<nTrain;i++){
      if (i!=id_Train && loc[i]>loc[id_Train] && (val==0||loc[i]<val))
        val=loc[i];
   }
   if (val>0.0)
        return val;
   else
        return loc[id_Train]+maIfNoTrainAhead;
}

